				current__et = STATE_idle;
				break;
			case STATE_idle_doorClosed:
				self->history[STATE_idle] = STATE_idle_doorClosed;
				current__et = STATE_idle;
				break;
			case STATE_goingDown:
				self->history[STATE_TOP] = STATE_goingDown;
				current__et = STATE_TOP;
				break;
			case STATE_goingDown_running:
				self->history[STATE_goingDown] = STATE_goingDown_running;
				current__et = STATE_goingDown;
				break;
			case STATE_goingDown_halted:
