/**
 * calls entry codes while entering a state's history. The ID of the final leaf state is returned
 * @param state__et - the state which is entered
 * @param handler__et - entry code is executed if not handler
 * @return - the ID of the final leaf state
 */
static etInt16 enterHistory(AElevatorMainController* self, etInt16 state__et, boolean handler__et) {
	boolean skip_entry__et = ET_FALSE;
	if (state__et >= STATE_MAX) {
		state__et = (etInt16) (state__et - STATE_MAX);
		skip_entry__et = ET_TRUE;
	}
	while (ET_TRUE) {
		switch (state__et) {
			case STATE_idle:
				if (!(skip_entry__et || handler__et)) entry_idle(self);
				/* state has a sub graph */
				/* with init transition */
				if (self->history[STATE_idle]==NO_STATE) {
					state__et = executeTransitionChain(self, CHAIN_idle_TRANS_INITIAL_TO__doorClosed, NULL, NULL);
				}
				else {
					state__et = self->history[STATE_idle];
				}
				break;
			case STATE_idle_doorOpen:
				if (!(skip_entry__et || handler__et)) entry_idle_doorOpen(self);
				/* in leaf state: return state id */
				return STATE_idle_doorOpen;
			case STATE_idle_doorClosed:
				if (!(skip_entry__et || handler__et)) entry_idle_doorClosed(self);
				/* in leaf state: return state id */
				return STATE_idle_doorClosed;
			case STATE_goingDown:
				/* state has a sub graph */
				/* without init transition */
				state__et = self->history[STATE_goingDown];
				break;
			case STATE_goingDown_running:
				if (!(skip_entry__et || handler__et)) entry_goingDown_running(self);
				/* in leaf state: return state id */
				return STATE_goingDown_running;
			case STATE_goingDown_halted:
				if (!(skip_entry__et || handler__et)) entry_goingDown_halted(self);
				/* in leaf state: return state id */
				return STATE_goingDown_halted;
			case STATE_goingUp:
				if (!(skip_entry__et || handler__et)) entry_goingUp(self);
				/* state has a sub graph */
				/* without init transition */
				state__et = self->history[STATE_goingUp];
				break;
			case STATE_goingUp_running:
				if (!(skip_entry__et || handler__et)) entry_goingUp_running(self);
				/* in leaf state: return state id */
				return STATE_goingUp_running;
			case STATE_goingUp_halted:
				if (!(skip_entry__et || handler__et)) entry_goingUp_halted(self);
				/* in leaf state: return state id */
				return STATE_goingUp_halted;
			case STATE_TOP:
				state__et = self->history[STATE_TOP];
				break;
			default:
				/* should not occur */
				break;
		}
		skip_entry__et = ET_FALSE;
	}
	/* return NO_STATE; // required by CDT but detected as unreachable by JDT because of while (true) */
}

static void AElevatorMainController_executeInitTransition(AElevatorMainController* self) {
	int chain__et = CHAIN_TRANS_INITIAL_TO__idle;
	etInt16 next__et = executeTransitionChain(self, chain__et, NULL, NULL);
	next__et = enterHistory(self, next__et, ET_FALSE);
	setState(self, next__et);
}

